
public class TurmDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int Zahl = 4;
		int i = 2;
		int tmpValue = 0;
		int ergebnis = Zahl;
		while (i <= 12)
		{
			tmpValue = ergebnis;
			ergebnis = tmpValue * i;
			System.out.println(tmpValue+" * "+i+" = "+ergebnis);
			i=i+1;
		}
	i = 2;
	while (i <= 9)
	{
		tmpValue = ergebnis;
		ergebnis = tmpValue / i;
		System.out.println(tmpValue+" / "+i+" = "+ergebnis);
		i=i+1;
	}
			
	
	}
		
	}

	
	
	

